import React from 'react'
import { useState } from 'react'
import FirstSection from './components/FirstSection'
import Cv from './components/Cv'
import SecondSection from './components/SecondsSection'
import ThirdSection from './components/ThirdSection'


const App = () => {

  const [count,setCounter] = useState(0)
  const [snd,setSnd] = useState(false)
  const [tasks, setTasks] = useState([])
  const [seconds, setSecond] = useState([])
  const [thirds, setThird] = useState([])
  const [isActive, setIsActive] = useState(true);
  const [isReady, setIsReady] = useState(false);
  const [isShwon, setIsShown] = useState(false);
  const [isCv, setIsCv] = useState(false);


  const addTask =  (task) => {
    setIsReady(current => !current);
    setIsActive(current => !current)
    setTasks(prevState => ({
      ...prevState,
      text: task.text,
      tel:task.tel,
      email: task.email,
      add: true
  }));
  }
  const addSecond =  (second) => {  
    setIsReady(current => !current);

    setSecond(prevState => ({
      ...prevState,
      school: second.school,
      title:second.title,
      date: second.date,
      finish: second.finish
  }));
  }
  const addThird =  (third) => {  
    setIsReady(current => !current);

    setThird(prevState => ({
      ...prevState,
      name: third.name,
      position:third.position,
      job: third.job,
      from: third.from,
      to: third.to

  }));
  }




  return (
      <div>

        {isActive &&  <FirstSection onAdd={addTask}/>}

  {isReady && <SecondSection onAdd={addSecond} />}
  <ThirdSection onAdd={addThird} />

   <Cv tasks={tasks} seconds={seconds} thirds={thirds}/>
      </div>
  )
}


export default App;
