import React from 'react'
import { useState } from 'react'
import FirstSection from './components/FirstSection'
import Cv from './components/Cv'
import SecondSection from './components/SecondsSection'


const App = () => {

  const [showButton,setShowButton] = useState(true)
  const [count,setCounter] = useState(0)
  const [tasks, setTasks] = useState([])
  const [seconds, setSecond] = useState('')

  const addTask =  (task) => {
    setTasks(prevState => ({
      ...prevState,
      text: task.school,
      tel:task.tel,
      email: task.email
  }));
  }
  const addSecond =  (second) => {  
    setSecond(prevState => ({
        ...prevState,
        school: second.school,
        title:second.title,
        date: second.date,
        finish:second.finish
    }));

  }

  return (
      <div>
   <FirstSection onAdd={addTask}/>
   <SecondSection onAdd={addSecond}/>

   <Cv tasks={tasks} seconds={seconds}/>
      </div>
  )
}


export default App;
